import react,{ useState, useEffect} from 'react'
import axios from 'axios';
import Gallerypersonajes from '../components/Gallerypersonajes'
import "./PersonajesPages.scss"
import Buscador from '../components/Buscador'
export default function Personajes (){
    
    let [personajes, setPersonajes] = useState([]) 
    
    console.log(personajes)
    useEffect(() => {
        
        const getPersonajes = async () =>{
            const res= await axios.get("https://api.got.show/api/show/characters/")
            setPersonajes (res.data)
            console.log(res.data)

        }
            getPersonajes();
        }, [])

        
            return (<div className="h-100"><div className="contenedorFull ">
            <Buscador 
                onSubmit={(data)=> {
                    personajes.filter(elem => elem.name === data.name)
                } }
            />

            <div className='personajes row'>
            
            {personajes.map((personaje)=>{
                return (<div className="col-2" key ={personaje.id}>
                        <Gallerypersonajes key ={personaje.id} personajes= {personaje} />
                        </div>)
            })
            }
            </div>
            </div>
            </div>)
}
