import {  Link, generatePath } from "react-router-dom";
import "./CronologiaPersonajes.scss"

export default function Cronologiapersonajes({personajes}){
    const linkStyle = {

        textDecoration: "none",
        color: 'white'
      };
    return(
    <Link to ={generatePath("/PersonajeDetail/:id",{id: personajes.name})}>
            <div className="galeriaP">
                <figure key={personajes.id}>
                    <h3 style={linkStyle}>{personajes.name}</h3>
                    <h3>[personajes.age]</h3>
                    <img className="imagenGaleria rounded-circle" src={personajes.image} alt={personajes.image} />


                </figure>
            </div>
    </Link>
    )
}
