import axios from "axios";
import react,{ useEffect,useState } from "react";
import { useParams } from "react-router-dom";
import personajes from "./PersonajesPages";
import "./PersonajeDetailPage.scss"
import VolverPersonajes from "../components/VolverPersonajes";
import VolverHome from "../components/VolverHome";


const PersonajeDetail = () => {
    
    //llamamos a la API para obtener el detalle del personaje por el nombre
    const urlPersonajeDetail = 'https://api.got.show/api/show/characters'
    const [PersonajeDetail, setPersonajeDetail] = useState ({});
    const urlCasasDetail = 'https://api.got.show/api/show/houses'
    const [CasasDetail, setCasasDetail] = useState ([]);
    let {id} = useParams();
    useEffect (() => {
        const getPersonajeDetail = async () =>{
            const res = await axios.get (urlPersonajeDetail+'/'+id)
            setPersonajeDetail(res.data)

        }
        getPersonajeDetail()
        const getCasasDetail = async () =>{
            console.log(PersonajeDetail.house)
            const res = await axios.get (urlCasasDetail+'/'+PersonajeDetail.house)
            setCasasDetail(res.data[0])

        }
        getCasasDetail()
    },[])
    
    console.log(PersonajeDetail)
    console.log(CasasDetail)
    let getURLCasas =() => {
        if (CasasDetail !== undefined) return CasasDetail.logoURL
    }

    //llamamos a la API para obtener la casa
    /*let CallHouse = (id2) => {
        const urlCasasDetail = 'https://api.got.show/api/show/houses'
        const [CasasDetail, setCasasDetail] = useState ([]);
        useEffect (() => {
            const getCasasDetail = async () =>{
                const res = await axios.get (urlCasasDetail+'/'+id2)
                console.log('res')
                console.log(res)

                setCasasDetail(res.data[0])

            }
            getCasasDetail()
            
        },[])
        console.log("GETCASASDETAIL")
        console.log(CasasDetail)
        console.log(id2)
    }*/
    
    

    return (<div className="personajesdetail h-100">
     <VolverPersonajes/>
     <VolverHome/>
    <div className="row marginNone">
        <div className="col-12 text-center">
        <img src={PersonajeDetail.image} alt={PersonajeDetail.image}/>
    <div className="separacion">{PersonajeDetail.name}</div> 
    </div>
    </div>
    <div className="row marginNone">
        <div className="col-2 text-center">
    <p className="letra1">House</p>
    <img src={getURLCasas()} alt={PersonajeDetail.logoURL}/>
</div>
        <div className="col-2 text-center">

    <p className="letra1">Allegiances</p>
        <div className="letra" >{PersonajeDetail.allegiances}</div> 
</div>
        <div className="col-2 text-center">

        <p className="letra1">Appearances</p>
        <div className="maxHeightAppearances letra" >{PersonajeDetail.appearances}</div> 
</div>
        <div className="col-2 text-center">

        <p className="letra1">Father</p>
        <div className="letra">{PersonajeDetail.father}</div> 
</div>
        <div className="col-2 text-center">

        <p className="letra1">Familie</p>
        <div className="letra">{PersonajeDetail.siblings}</div> 
</div>
        <div className="col-2 text-center">

        <p className="letra1" >Titles</p>
        <div className="letra">{PersonajeDetail.titles}</div> 
        </div>
</div>

    </div>
    )
}





export default PersonajeDetail
